import React, { useState, useEffect } from 'react';
import { Text, View, Modal, Pressable } from 'react-native';
import MapView, { Marker } from 'react-native-maps';
import * as Location from 'expo-location';
import * as Permissions from 'expo-permissions';
import { GooglePlacesAutocomplete } from 'react-native-google-places-autocomplete';
import { homeStyle } from './style';
import { useNavigation } from '@react-navigation/native';
import { FAB } from 'react-native-paper';
import { MyComponent } from '../../components/fabButton'
import api from '../service/api';
import MarkerMap from '../../components/markerMap';


export default function Home() {

  const [modalAnuncio, setModalAnuncio] = useState(false);

  const navigation = useNavigation();

  const irToAnuncio = () => {
    navigation.navigate('Anuncio', { photosir: [] });
  }

  const [anuncios, setAnuncios] = useState([]);

  const [origin, setOrigin] = useState(null);

  useEffect(() => {
    (async function () {
      const { status, permissions } = await Permissions.askAsync(Permissions.LOCATION);
      if (status === 'granted') {
        let location = await Location.getCurrentPositionAsync({ enableHighAccuracy: true });
        setOrigin({
          latitude: location.coords.latitude,
          longitude: location.coords.longitude,
          latitudeDelta: 0.0143,
          longitudeDelta: 0.0134,
        })


        api.get("anuncio").then((response) => {
          console.log('Busca anúncios com sucesso!' + response.data)
          setAnuncios(response.data);
        });

      } else {
        throw new Error('Location permission not granted');
      }
    })();
  }, [])

  return (
    <View>


      <View style={homeStyle.filtro}>

        <GooglePlacesAutocomplete
          placeholder='Search'
          fetchDetails={true}
          onPress={(data, details = null) => {

            setOrigin({
              latitude: details.geometry.location.lat,
              longitude: details.geometry.location.lng,
              latitudeDelta: 0.0143,
              longitudeDelta: 0.0134,
            })

          }}
          query={{
            key: 'AIzaSyCqtH6mpePIrYeZmzyDdGktYIuiLtT4mfE',
            language: 'pt-br',
          }}
        />


      </View>

      <MapView
        style={homeStyle.map}
        region={origin}
        showsUserLocation={true}
        loadingEnabled={true}
      >

        {
          anuncios.map((anuncio, index) => (
            <MarkerMap key={index} lat={anuncio.casa.lat} lng={anuncio.casa.lng} titulo={anuncio.titulo} valorAluguel={anuncio.casa.valorAluguel} />
          ))
        }

      </MapView>

      <View style={homeStyle.fabView}>

        <FAB
          small
          icon="plus"
          style={homeStyle.fab}
          onPress={irToAnuncio}
        />

      </View>


      <Modal
        animationType="slide"
        transparent={true}
        visible={modalAnuncio}
        onRequestClose={() => {
          Alert.alert("Modal has been closed.");
          setModalAnuncio(!modalAnuncio);
        }}
      >
        <View style={homeStyle.centeredView}>
          <View style={homeStyle.modalView}>
            <Text style={homeStyle.modalText}>Hello World!</Text>
            <Pressable
              style={[homeStyle.button, homeStyle.buttonClose]}
              onPress={() => setModalAnuncio(!modalAnuncio)}
            >
              <Text style={homeStyle.textStyle}>Hide Modal</Text>
            </Pressable>
          </View>
        </View>

      </Modal>




    </View>

  );
}
