import { Button, Card, TextInput } from 'react-native-paper';
import { StatusBar } from 'expo-status-bar';
import React, { useState, Component, useEffect } from 'react';
import { StyleSheet, Text, ScrollView, View, Dimensions, FlatList, Image } from 'react-native';
import SimpleSelectButton from 'react-native-simple-select-button';
import { CheckBox } from 'react-native-elements';
import { anuncioStyle } from './anuncioStyle';
import { Formik, Field } from 'formik';
import api from '../service/api';
import apiExterno from '../service/apiExterno';
import { useNavigation } from '@react-navigation/native';
import axios from 'axios';

export default function Anuncio(props) {

  const [isTipoImovel, setTipoImovel] = useState(false);
  const [isTemGaragem, setTemGaragem] = useState(false);
  const [isTemPet, setTemPet] = useState(false);

  const navigation = useNavigation();

  
  const photoss = props.route.params.photosir;
 

  const renderImage = (item, i) => {
    return (
      <Image
        style={{ height: 100, width: 100 }}
        source={{ uri: item.uri }}
        key={i}
      />
    )
  }

  const pegarEnd = (cep) => {
    console.log(cep);
  /*  apiExterno.get(`https://maps.googleapis.com/maps/api/geocode/json?address=/${cep}/&key=AIzaSyC5W7_73I48OdD2m-SLOxRMvV1zqhl333c`).then((dados) => {
      console.log('Busca de cep foif');

      console.log("lat" + dados);
      console.log("lngd" + dados.results[0].formatted_address);

    });*/
    const getData = async () => {
      const result = await axios.get(`https://viacep.com.br/ws/87055060/json/`)
      console.log(result.data.logradouro);
    }
    getData();
   
  }


  return (
    <ScrollView style={anuncioStyle.container}>
      <Formik
        initialValues={{
          titulo: '',
          tipoImovel: '',
          cep: '',
          endereco: '',
          numero: '',
          numeroVagas: '',
          descricao: '',
          temGaragem: '',
          permitidoPets: '',
          valorAluguel: ''
        }}

        onSubmit={(values) => {

          values.temGaragem = isTemGaragem;
          values.permitidoPets = isTemGaragem;

          if (isTipoImovel) {
            values.tipoImovel = 'APARTAMENTO';
          } else {
            values.tipoImovel = 'CASA';
          }

          api.post("anuncio", values).then((response) => {
            navigation.navigate('Entrar');
            console.log('An√∫ncio Salvo com sucesso!' + response.data)
          });

        }}

    
      >

        {(props) => (

          <View>
            <TextInput label="T√≠tulo do An√∫ncio"
              onChangeText={props.handleChange('titulo')}
              value={props.values.titulo} />

            <Card style={anuncioStyle.container} style={anuncioStyle.distancia}>
              <Text style={anuncioStyle.textStyleCard} >Tipo do im√≥vel?</Text>
              <CheckBox
                center
                title='Apartamento'
                checkedIcon='dot-circle-o'
                uncheckedIcon='circle-o'
                checked={isTipoImovel}
                onPress={() => setTipoImovel(true)}
              />

              <CheckBox
                center
                title='Casa'
                checkedIcon='dot-circle-o'
                uncheckedIcon='circle-o'
                onPress={() => setTipoImovel(false)}
                checked={!isTipoImovel}
              />
            </Card>

            <TextInput style={anuncioStyle.distancia} label="CEP"
              onChangeText={props.handleChange('cep')}
              onBlur={() => pegarEnd(props.values.cep)}
              value={props.values.cep} />

            <TextInput style={anuncioStyle.distancia} label="Endere√ßo"
              onChangeText={props.handleChange('endereco')}
              value={props.values.endereco} />

            <TextInput style={anuncioStyle.distancia} label="N√∫mero" keyboardType="phone-pad"
              onChangeText={props.handleChange('numero')}
              value={props.values.numero}
            />

            <TextInput style={anuncioStyle.distancia} label="N√∫mero de vagas" keyboardType="phone-pad"
              onChangeText={props.handleChange('numeroVagas')}
              value={props.values.numeroVagas}
            />

            <TextInput style={anuncioStyle.distancia} label="Descri√ß√£o"
              onChangeText={props.handleChange('descricao')}
              value={props.values.descricao} />

            <Card style={anuncioStyle.container} style={anuncioStyle.distancia} >
              <Text style={anuncioStyle.textStyleCard} >Tem Garagem?</Text>
              <CheckBox
                style={anuncioStyle.selectStyle}
                center
                title='Sim'
                checkedIcon='dot-circle-o'
                uncheckedIcon='circle-o'
                checked={isTemGaragem}
                onPress={() => setTemGaragem(true)}
              />

              <CheckBox
                center
                title='N√£o'
                checkedIcon='dot-circle-o'
                uncheckedIcon='circle-o'
                onPress={() => setTemGaragem(false)}
                checked={!isTemGaragem}
              />
            </Card>

            <Card style={anuncioStyle.container} >
              <Text style={anuncioStyle.textStyleCard}>S√£o permitido Pets?</Text>
              <CheckBox
                center
                title='Sim'
                checkedIcon='dot-circle-o'
                uncheckedIcon='circle-o'
                checked={isTemPet}
                onPress={() => setTemPet(true)}
              />

              <CheckBox
                center
                title='N√£o'
                checkedIcon='dot-circle-o'
                uncheckedIcon='circle-o'
                onPress={() => setTemPet(false)}
                checked={!isTemPet}
              />
            </Card>

            <TextInput style={anuncioStyle.distancia} label="Valor do aluguel" keyboardType="phone-pad"
              onChangeText={props.handleChange('valorAluguel')}
              value={props.values.valorAluguel}
            />

            <View style={anuncioStyle.content}>
              <Button mode="contained" style={anuncioStyle.button}
                onPress={() => {
                  navigation.navigate('ImageScreen')
                }}>Selecione as Imagens</Button>
              <ScrollView>
                {
                  photoss.map((item, i) => (
                    <Image
                      style={{ height: 100, width: 100 }}
                      source={{ uri: item.uri }}
                      key={i}
                    />
                  ))}



              </ScrollView>
            </View>

            <Button mode="contained" style={anuncioStyle.cardButton} onPress={props.handleSubmit}>Registrar an√∫ncio</Button>

          </View>
        )}


      </Formik>

    </ScrollView>
  );
}
